(1, 1, 4, 'HWaCYxhr', 'MF Logo', 0, '', '<div class="scsCoverOverlay"></div>\r\n<div class="container scsContent">\r\n	<div class="scsEl scsElImg scsElWithArea" data-el="img">\r\n      <div class="scsElArea"><img src="[SCS_ASSETS_URL]img/blocks/mf-logo/logo.png" /></div>\r\n  </div>\r\n</div>', '/*base block setting*/\r\n#{{block.view_id}} {\r\n  {% if block.params.bg_img_enb.val %}\r\n  	background: url("{{ block.params.bg_img.val }}") no-repeat center center; \r\n    -webkit-background-size: cover;\r\n    -moz-background-size: cover;\r\n    -o-background-size: cover;\r\n    background-size: cover;\r\n  	filter: progid:DXImageTransform.Microsoft.AlphaImageLoader(src=''{{ block.params.bg_img.val }}'', sizingMethod=''scale'');\r\n-ms-filter: "progid:DXImageTransform.Microsoft.AlphaImageLoader(src=''{{ block.params.bg_img.val }}'', sizingMethod=''scale'')";\r\n  {% endif %}\r\n}\r\n/*overlay*/\r\n#{{block.view_id}} .scsCoverOverlay {\r\n	position: absolute;\r\n  	width: 100%;\r\n  	height: 100%;\r\n  	top: 0;\r\n  	left: 0;\r\n  	background-color: {{ block.params.fill_color.val }};\r\n  	opacity: {{ block.params.fill_color_opacity.val }};\r\n    {% if not block.params.fill_color_enb.val %}\r\n    display: none;\r\n    {% endif %}\r\n}\r\n/*align*/\r\n#{{block.view_id}}.scsAlign_center .scsContent {\r\n	text-align: center;\r\n}\r\n#{{block.view_id}}.scsAlign_left .scsContent {\r\n	text-align: left;\r\n}\r\n#{{block.view_id}}.scsAlign_right .scsContent {\r\n	text-align: right\r\n}\r\n/*main styles*/\r\n#{{block.view_id}} .scsContent {\r\n	padding-top: 25px;\r\n  	padding-bottom: 25px;\r\n}', 'mf-logo.jpg', 1, 1, 0, '2015-09-04 23:17:23'),
(2, 1, 4, 'yxtx9g3O', 'MF Header', 0, '', '<div class="scsCoverOverlay"></div>\r\n<div class="container scsContent">\r\n	<div class="scsEl scsMainText" data-el="txt">\r\n      <p><span style="color: rgb(255, 255, 255); font-size: 36pt;" data-mce-style="color: #ffffff; font-size: 36pt;">We are currently <strong>working on</strong>.</span></p>\r\n  	</div>\r\n  	<div class="scsEl scsElWithArea" data-el="progress_bar">\r\n      <div class="scsElArea scsProgrBarShell">\r\n  		<div class="scsPointerProgrBar">\r\n          	<div class="scsValueShellProgrBar">\r\n        		<span class="scsValueProgrBar"></span>%\r\n          	</div>\r\n        </div>\r\n      	<div class="scsFillProgrBar"></div>\r\n      </div>\r\n  	</div>\r\n</div>', '/*base block setting*/\r\n#{{block.view_id}} {\r\n  {% if block.params.bg_img_enb.val %}\r\n  	background: url("{{ block.params.bg_img.val }}") no-repeat center center; \r\n    -webkit-background-size: cover;\r\n    -moz-background-size: cover;\r\n    -o-background-size: cover;\r\n    background-size: cover;\r\n  	filter: progid:DXImageTransform.Microsoft.AlphaImageLoader(src=''{{ block.params.bg_img.val }}'', sizingMethod=''scale'');\r\n-ms-filter: "progid:DXImageTransform.Microsoft.AlphaImageLoader(src=''{{ block.params.bg_img.val }}'', sizingMethod=''scale'')";\r\n  {% endif %}\r\n}\r\n/*overlay*/\r\n#{{block.view_id}} .scsCoverOverlay {\r\n	position: absolute;\r\n  	width: 100%;\r\n  	height: 100%;\r\n  	top: 0;\r\n  	left: 0;\r\n  	background-color: {{ block.params.fill_color.val }};\r\n  	opacity: {{ block.params.fill_color_opacity.val }};\r\n    {% if not block.params.fill_color_enb.val %}\r\n    display: none;\r\n    {% endif %}\r\n}\r\n/*align*/\r\n#{{block.view_id}}.scsAlign_center .scsContent {\r\n	text-align: center;\r\n}\r\n#{{block.view_id}}.scsAlign_left .scsContent {\r\n	text-align: left;\r\n}\r\n#{{block.view_id}}.scsAlign_right .scsContent {\r\n	text-align: right\r\n}\r\n/*ignore p margin*/\r\n#{{block.view_id}} p {\r\n	ma